TilePage page = Workspace.Monitors[0].Pages[0] as TilePage;
ContentGroup contentGroup = page.States[0].Content;
Content content = contentGroup.Current;
MapContent mapContent = content as MapContent;
mapContent.Center = new GeoCoordinate(0.0, 0.0);

//Open the monitoring task in SD and put a Map in the the tile 0
TilePage page = Workspace.Monitors[0].Pages[0] as TilePage;
ContentGroup contentGroup = page.States[0].Content;
Content content = contentGroup.Current;
MapContent mapContent = content as MapContent;
mapContent.ViewArea = new GeoBounds(new GeoCoordinate(0, 0), 0.5, 0.5);

