Camera camera = (Camera)Workspace.Sdk.GetEntity(EntityType.Camera, 1);
var service = Workspace.Services.Get<IContentBuilderService>();
var contentGroup = service?.Build(camera.Guid);
if (contentGroup != null)
{
    MonitorPage page = Workspace.DefaultMonitor.Pages[0] as MonitorPage;
    TileState state = page?.States[0];
    state.Content = contentGroup;
}
