example.code:
ActiveDirectoryRole adRole = m_engine.GetEntity(new Guid("493D73FA-2725-4251-ABF7-4A85DE52DE55")) as ActiveDirectoryRole;
if (adRole != null)
{
    Collection<ImportedActiveDirectoryGroupInfo> imported = adRole.ImportedGroups;
    foreach (ImportedActiveDirectoryGroupInfo imp in imported)
    {
        if (imp.GroupAccountName == "NameOfGroup")
        {
            //modifying the group here will only modify the copy
            imp.SynchronizeUserGroup = true;
            imp.CreateUserOnFirstLogon = false;
            imp.SynchronizeCardholders = true;
            imp.SynchronizeCredentials = true;
        }
    }    
    ImportedActiveDirectoryGroupInfo adgroupInfo = new ImportedActiveDirectoryGroupInfo(new Guid("{a6d70acd-4f89-4315-b276-b38ac82f0713}"),"SDK",true,true,false,false);
    //Here we are adding a group info to a copy of the collection
    imported.Add(adgroupInfo);
    m_engine.TransactionManager.CreateTransaction();
    //Here we are modifying the internal collection using the set
    adRole.ImportedGroups = imported;
    m_engine.TransactionManager.CommitTransaction(true);
}

