ActiveDirectoryRole activeDirectoryRole = m_engine.GetEntity(new Guid("253EB680-F63B-423A-B7FC-1354F76B3B09")) as ActiveDirectoryRole;      
if(activeDirectoryRole!=null)
{
    ReadOnlyCollection<ImportedActiveDirectoryGroupInfo> importedGroupInfo = activeDirectoryRole.ImportedGroups;
    foreach (ImportedActiveDirectoryGroupInfo imp in importedGroupInfo)
    {
        if(imp.GroupAccountName == "NameOfGroup")
        {
            //Set to true if you want to synchronize the user groups
            imp.SynchronizeUserGroup = true;
            //CreateUserOnFirstLogon is activated because SynchronizeUserGroup is enabled

            imp.CreateUserOnFirstLogon = true;
            //set to true or false the next two properties
            imp.SynchronizeCardholders = true;
            imp.SynchronizeCredentials = false;
        }               
    }
    //create and commit a transaction
    m_engine.TransactionManager.CreateTransaction();
    activeDirectoryRole.ImportedGroups = importedGroupInfo;
    m_engine.TransactionManager.CommitTransaction(true);
}

ActiveDirectoryRole activeDirectoryRole = m_engine.GetEntity(new Guid("253EB680-F63B-423A-B7FC-1354F76B3B09")) as ActiveDirectoryRole;      
