example.code:
//Retrieve the entity that have the eventToAction 
User admin = m_engine.GetEntity(new Guid("00000000-0000-0000-0000-000000000003")) as User;
//Use this entity to retrieve the EventToActions associated with "All Entities" from config tool 
SystemConfiguration systemconfiguration = m_engine.GetEntity(new Guid("00000000-0000-0000-0000-000000000007")) as SystemConfiguration;
//Event userLoggedOnEvent = m_engine.ActionManager.BuildEvent(EventType.UserLoggedOn, new Guid("00000000-0000-0000-0000-000000000003"));
Schedule weekly = m_engine.GetEntity(new Guid("54274602-0175-4eef-9c27-9904d3642104")) as Schedule;
//Create the action using BuildAction
TriggerAlarmAction action = m_engine.ActionManager.BuildAction(ActionType.TriggerAlarm, new Guid("373b3303-a9c0-4bb1-ad2b-43327d8e19ca"), weekly.Guid) as TriggerAlarmAction;
//Retrieve the EventToActionCollection from the entity
EventToActionCollection eventToActionCollection = admin.EventToActions;
//This can be used to retrieve the EventToActions from "All Entities".
//"All Entities" in Config Tool represent the SystemConfiguration Entity.
EventToActionCollection systemConfigurationCollection = systemconfiguration.EventToActions;
//Add the Event and the Action to the collection
EventToAction eventToAction = eventToActionCollection.Add(EventType.UserLoggedOn, action);

//Retrieve the entity that have the eventToAction 
User admin = m_engine.GetEntity(new Guid("00000000-0000-0000-0000-000000000003")) as User;
//Use this entity to retrieve the EventToActions associated with "All Entities" from config tool 
SystemConfiguration systemconfiguration = m_engine.GetEntity(new Guid("00000000-0000-0000-0000-000000000007")) as SystemConfiguration;
//Event userLoggedOnEvent = m_engine.ActionManager.BuildEvent(EventType.UserLoggedOn, new Guid("00000000-0000-0000-0000-000000000003"));
Schedule weekly = m_engine.GetEntity(new Guid("54274602-0175-4eef-9c27-9904d3642104")) as Schedule;
//Create the action using BuildAction
TriggerAlarmAction action = m_engine.ActionManager.BuildAction(ActionType.TriggerAlarm, new Guid("373b3303-a9c0-4bb1-ad2b-43327d8e19ca"), weekly.Guid) as TriggerAlarmAction;
//Retrieve the EventToActionCollection from the entity
EventToActionCollection eventToActionCollection = admin.EventToActions;
//This can be used to retrieve the EventToActions from "All Entities".
//"All Entities" in Config Tool represent the SystemConfiguration Entity.
EventToActionCollection systemConfigurationCollection = systemconfiguration.EventToActions;
//Add the Event and the Action to the collection
EventToAction toDelete=null;
foreach (EventToAction ev in eventToActionCollection)
{
    if(ev.Action is TriggerAlarmAction)
    {
        toDelete = ev;
    }      
}
if(toDelete!=null)
{
    //This method cannot be called while iterating through the collection.
    eventToActionCollection.Remove(toDelete);
}

