//Basic utilisation of the , to create a single custom event for the  .
public static async Task CreateCustomField(Engine engine)
{
    var systemConfiguration = engine.GetEntity{SystemConfiguration}(SdkGuids.SystemConfiguration);
    var customEventService = systemConfiguration.CustomEventService;

    var customEventBuilder = customEventService.CreateCustomFieldBuilder()
                                               .SetName("CustomFieldUniqueName")
                                               .SetEntityType(EntityType.Door);

    var customEvent = customEventBuilder.Build();
    await customEventService.AddAsync(customEvent);
}

//Using the  as a factory.
public static async Task AlarmCustomFieldFactory(Engine engine, params string[] names)
{
    var systemConfiguration = engine.GetEntity{SystemConfiguration}(SdkGuids.SystemConfiguration);
    var customEventService = systemConfiguration.CustomEventService;

    var customEventBuilder = customEventService.CreateCustomEventBuilder()
                                               .SetName("CustomFieldUniqueName")
                                               .SetEntityType(EntityType.Alarm);

    var customEvents = names.Select(name => customFieldBuilder.SetName(name).Build())
                            .ToArray();

    await customEventService.AddAsync(customEvent); 
}

//Basic utilisation of the , to create a single custom field for the  .
public static async Task CreateCustomField(Engine engine)
{
    var systemConfiguration = engine.GetEntity{SystemConfiguration}(SdkGuids.SystemConfiguration);
    var customFieldService = systemConfiguration.CustomFieldService;

    var customFieldBuilder = customFieldService.CreateCustomFieldBuilder()
                                               .SetName("CustomFieldUniqueName")
                                               .SetEntityType(EntityType.Door)
                                               .SetValueType(CustomFieldValueType.Text)
                                               .SetDefaultValue(string.Empty);

    var customField = customFieldBuilder.Build();
    await customFieldService.AddCustomFieldAsync(customField);
}

//Using the  as a factory.
public static async Task AlarmCustomFieldFactory(Engine engine, params string[] names)
{
    var systemConfiguration = engine.GetEntity{SystemConfiguration}(SdkGuids.SystemConfiguration);
    var customFieldService = systemConfiguration.CustomFieldService;

    var customFieldBuilder = customFieldService.CreateCustomFieldBuilder()
                                               .SetEntityType(EntityType.Alarm)
                                               .SetValueType(CustomFieldValueType.Text)
                                               .SetDefaultValue(string.Empty); 

    var customFields = names.Select(name => customFieldBuilder.SetName(name).Build())
                            .ToArray();

    await customFieldService.AddCustomFieldAsync(customFields); 
}

var cardholder = _engine.GetEntity(guid) as Cardholder;
if (cardholder != null)
{
    var systemConfiguration = _engine.GetEntity(SdkGuids.SystemConfiguration) as SystemConfiguration;
    if (systemConfiguration != null)
    {
        var customFields = systemConfiguration.CustomFields;
        if (customFields != null)
        {
            foreach (CustomField customField in customFields)
            {
                if (!cardholder.CustomFields.IsCustomFieldFromActiveDirectory(cardholder.Guid, customField))
                {
                    if (cardholder.CustomFields[customField.Name] is string)
                        cardholder.CustomFields[customField.Name] = "No time to say hello";
                }
            }
        }
    }
}

