//This will modify the center and view area of the geographical map.
GeoCoordinate center = new GeoCoordinate(45.494818, -73.754897);
double scale = 0.04;//only used when the map is BeNomad
GeoCoordinate topLeft = new GeoCoordinate(45.500413, -73.766995);
GeoCoordinate bottomRight = new GeoCoordinate(45.494433, -73.753476);
GeoBounds bounds = new GeoBounds(topLeft,bottomRight);
benomadMap.DefaultView = new GeoView(center, scale, bounds);

Create an area and the associated google map with it.
Area area = m_engine.CreateEntity("Area" + Guid.NewGuid(), EntityType.Area) as Area;
Map googleMap = (Map)area.CreateMap(new GeographicalMapCreationInfo(MapProviders.Google));
GeoCoordinate center = new GeoCoordinate(45.492111, -73.791182);
GeoCoordinate topLeft = new GeoCoordinate(45.494791, -73.796881);
GeoCoordinate bottomRight = new GeoCoordinate(45.489755, -73.785826);
GeoBounds bounds = new GeoBounds(topLeft, bottomRight);
googleMap.DefaultView = new GeoView(center, 1, bounds);

//Example on how to change the recording mode to ContinuousRecording

//.. initialisation and connection to the engine.

//Then get the archiver role.
var archiver = engine.GetEntity{ArchiverRole}(new Guid("archiver_role_guid"));

//Then get the recording configuration for that archiver
var recordingConfiguration = archiver.RecordingConfiguration;

//Then we change the recording configuration to ContinuousRecording
recordingConfiguration.SetScheduledRecordingModes(ScheduledRecordingMode.ContinuousRecording);

