//Make sure all the needed entities are in the Entity Cache before calling GetEntity
//Call the GetEntity to retrieve the Camera
Camera camera = m_engine.GetEntity(new Guid("00000001-0000-babe-0000-00408c730d81")) as Camera;
ReadOnlyCollection<Guid> streams = camera.Streams;
foreach(Guid guid in streams)
{
    //Get the VideoStreams
    VideoStream stream = m_engine.GetEntity(guid) as VideoStream;
    if (stream != null)
    {
        string name = stream.Name;
        if (name.Equals("MPEG-4 - 1"))
        {
            //Important to modify these settings in a transaction
            m_engine.TransactionManager.CreateTransaction();

            //This will set the Recording frame rate of the stream
            stream.SetRecordingFrameInterval(Schedule.AlwaysScheduleGuid, RecordingFrameInterval.RecordAllFrames);
            //Sets the Key frame interval of of the stream
            stream.SetKeyFrameInterval(Schedule.AlwaysScheduleGuid,10);
            //Sets the frame rate (value in frame per second)
            stream.SetFrameRate(Schedule.AlwaysScheduleGuid,5);
            //Sets the bit rate (the Constant bit rate has to be enabled)
            stream.SetBitRate(Schedule.AlwaysScheduleGuid,18);
            //Find all the available capabilities of the stream
            VideoCompressionCapabilities capabilities = stream.VideoCompressionCapabilities;
            //retrieve the supported resolutions available
            List<StreamSupportedResolution> streamRes = capabilities.SupportedResolutions;
            if(streamRes.Count>0)
            {
                //This should set the resolution of the Stream to the first available in the drop down menu
                stream.SetResolution(Schedule.AlwaysScheduleGuid, streamRes[0]);
            }

            m_engine.TransactionManager.CommitTransaction(true);
        }
    }
}

