//Retrive the camera for which you want to modify its motion detection
Camera camera = m_engine.GetEntity(new Guid("00000001-0000-babe-0000-00408c730d81")) as Camera;
//This will return a different Motion Detection Configuration for every schedule.
ReadOnlyCollection<IMotionDetectionConfiguration> motionDetectionConfigurations = camera.MotionDetectionConfigurations;
foreach(IMotionDetectionConfiguration configuration in motionDetectionConfigurations)
{
    //Modify the sensitivity
    configuration.Sensitivity = 23;
    //Change the detection to be done on the unit
    configuration.MotionDetectionType = MotionDetectionType.Unit;
    configuration.ConsecutiveFrameHits = 3;
    //This will get all the motion zones of the motion detection.
    ReadOnlyCollection<IMotionDetectionZoneConfiguration> allzones = configuration.Zones;
    foreach (IMotionDetectionZoneConfiguration motion in allzones)
    {
        //change the zone configuration
        motion.MotionOnThreshold = 10;
        motion.MotionOffThreshold = 20;
    }
    //Important, call this method for the changes to take place
    camera.UpdateMotionDetectionConfiguration(configuration);
}

