//The child gets inserted into the partition of the parent.
partitionChild.InsertIntoPartition(partitionParent.Guid);
m_engine.TransactionManager.CommitTransaction(true);

public string ClearCabinReader(int elevatorLogicalId)
{
   string returnValue = null;
   Engine.QueueUpdateAndWait(() =>
   {
       try
       {
           Engine.TransactionManager.CreateTransaction();
           Elevator elevator = Engine.GetEntity(EntityType.Elevator, elevatorLogicalId) as Elevator;
           elevator.ClearCabinReader();
           Engine.TransactionManager.CommitTransaction();
           returnValue = @"ClearCabinReader - Success";
       }
       catch (Exception ex)
       {
           lock (Engine.TransactionManager.SyncUpdate)
           {
               if (Engine.TransactionManager.IsTransactionActive)
               {
                   Engine.TransactionManager.RollbackTransaction();
               }
           }
           returnValue = @"ClearCabinReader - Exception: " + ex;
       }
   });
   return returnValue;
}

 public string SetCabinReader(int elevatorLogicalId, Guid readerId)
 {
    string returnValue = null;
    Engine.QueueUpdateAndWait(() =>
    {
        try
        {
            Engine.TransactionManager.CreateTransaction();
            Elevator elevator = Engine.GetEntity(EntityType.Elevator, elevatorLogicalId) as Elevator;            
            elevator.SetCabinReader(readerId);

            Engine.TransactionManager.CommitTransaction();
            returnValue = @"SetCabinReader - Reader set.";

        }
        catch (Exception exception)
        {
            if (Engine.TransactionManager.IsTransactionActive)
            {
                Engine.TransactionManager.RollbackTransaction();
            }

            returnValue = "SetCabinReader - Error: " + exception;
        }

    });

    return returnValue;
}

