// SET EXAMPLE
public string SetOutputToActive(int unitLogicalId, int outputId)
{
    string returnValue = null;
    try
    {
        Unit unit = Engine.GetEntity(EntityType.Unit, unitLogicalId) as Unit;
        if (unit == null)
        {
           returnValue = @"SetOutputToActive - Unit not found";
           return;
        }
        OutputDevice output = Engine.GetEntity(unit.Devices[outputId]) as OutputDevice;
        if (output != null)
        {
            output.State = OutputState.Active;
            returnValue = "SetOutputToActive - Success";
        }
    }
    catch (Exception exception)
    {
        returnValue = "SetOutputToActive - exception: " + exception;
    }
}

