private void ChangePicture(Guid cardholderGuid, Image cardholderPicture)
{
    // Get the entity that represents the imported from Active Directory cardholder
    var cardholderFromAD = _engine.GetEntity(cardholderGuid) as Cardholder;

    // Set the picture if allowed
    if (cardholderFromAD != null && cardholderFromAD.CanSetPicture())
