Schedule schedule = m_engine.GetEntity((Guid)dr[0]) as Schedule;
if (schedule != null)
{
    DailyCoverage dailyCoverage = schedule.Coverage as DailyCoverage;
    if (dailyCoverage != null)
    {
        //A Coverage can contain one or more CoverageItems
        foreach (var dailyCoverageItem in dailyCoverage.CoverageItems)
        {
            //Here access or modify dailyCoverageItems
            //Changes will not be saved to the directory here since it's a copy
        }

        //cannot change the default entity Schedule named "Always"
        if(schedule.Name!="Always")
        {
            m_engine.TransactionManager.CreateTransaction();
            dailyCoverage.Add(new DailyCoverageItem(SdkTime.Now, SdkTime.Now));
            schedule.Coverage = dailyCoverage;
            m_engine.TransactionManager.CommitTransaction(true);
        }
    }
}

