User user = m_engine.GetEntity(new Guid("C6AEDEC0-1B17-40D9-827E-E8DF1B8D3F45")) as User;
if(user!=null)
{
    //tsBefore will be 0 days, 0 hours, 0 minutes and 0 seconds when it's not activated
    TimeSpan tsBefore = user.ArchiveViewingLimitation;
    //Create the desired TimeSpan
    TimeSpan tsAfter = new TimeSpan(10,0,0,0);
    //This should be done in a transaction when we modify more than 1 ArchiveViewingLimitation
    user.ArchiveViewingLimitation = tsAfter;
    user.IsArchiveViewingLimitationInherited = false;
    //At this point the ArchiveViewingLimitation is activated
}

User user = m_engine.GetEntity(new Guid("C6AEDEC0-1B17-40D9-827E-E8DF1B8D3F45")) as User;
if(user!=null)
{
    //by setting it to false, the User will have his own ArchiveViewingLimitation
    user.IsArchiveViewingLimitationInherited = false;

    //make a limit of 20 days
    TimeSpan limitation = new TimeSpan(20,0,0,0);
    user.ArchiveViewingLimitation = limitation;
}

//Here you find a user that you wish to modify it's Security Level
User user = m_engine.GetEntity(new Guid("C91A3F56-4EDC-46F3-B502-E63984FFE487")) as User;
if (user != null)
{
    //to get the user's current Security Level
    byte securitylevel = user.SecurityLevel;
    //Create a transaction
    m_engine.TransactionManager.CreateTransaction();
    user.SecurityLevel = 230;
    //for the newly set SecurityLevel to be effective, you shouldn't inherit them from the parent
    user.IsSecurityLevelInherited = false;
    //commit the change
    m_engine.TransactionManager.CommitTransaction(true);
}

