member.example:
//This is the way to create and send a HitByIdQuery.
//Create the HitByIdQuery.
HitByIdQuery hitById = (HitByIdQuery)m_sdkEngine.ReportManager.CreateReportQuery(ReportType.LprHitById);
//Hook the event to receive the response.
hitById.QueryCompleted += OnHitByIdQueryCompleted;
//The guid of the Hit. Replace {GUID} by the guid value for the id of the hit.
hitById.HitId = new Guid("{GUID}");
//The guid of the source. Replace {GUID} by the guid of the LPR unit.
hitById.UnitSource = new Guid("{GUID}");
//Ajust the time range for the hit. The hit must be in the time range specified.
hitById.TimeRange.SetTimeRange(DateTime.UtcNow.AddDays(-4));
//Send the query. Note that the first argument is null because we hooked manualy the event after the creation of the query.
hitById.BeginQuery(null, null);

