AlarmActivityQueryCompletedEventArgs result = query.Query() as AlarmActivityQueryCompletedEventArgs;
if((result != null) &&  (result.Success))
{
    foreach (DataRow dr in result.Data.Rows)
    {
        int instanceId = (int)dr[0];
        Guid alarmGuid = (Guid)dr[1];
        Guid triggerEvent = (Guid)dr[2];
        string dynamicContext = (string)dr[16];
        //To retrieve the list of urls
        foreach (DataRow row in result.DynamicUlrsData.Rows)
        {
            int urlInstanceId = (int)row[0];
            if (instanceId == urlInstanceId)
            {
                string url = (string)row[1];
            }
        }
        //to retrieve the list of attached entities
        foreach (DataRow row in result.DynamicAttachedEntitiesData.Rows)
        {
            int urlInstanceId = (int)row[0];
            if (instanceId == urlInstanceId)
            {
                Guid entityGuid = (Guid)row[1];
                Entity entity = m_engine.GetEntity(entityGuid);
            }
        }
    }
}

