members.member:
Throws a custom event containing a message.
/// In order to receive a Custom Event it's Source Entity must be preloaded in memory. This function is now set to obsolete, since there is no SourceEntity passed by default the logged on user was set as the SourceEntity, this meant that a preload on that user entity is needed in order to receive the event by any other logged on user.
The custom event id The custom event message

example.code:
Camera camera = m_engine.GetEntity(new Guid("00000001-0000-babe-0000-00408c730d81")) as Camera;
CustomEventInstance myEvent = m_engine.ActionManager.BuildEvent(EventType.CustomEvent, camera.Guid) as CustomEventInstance;
myEvent.Id = new CustomEventId(130);
myEvent.Message = "Message 1";
m_engine.ActionManager.RaiseEvent(myEvent);

public static void CreateAndFireEvents(IEngine engine, IEnumerable<EventType> eventTypes, Guid source)
{
    using (EventTransaction transaction = engine.TransactionManager.CreateEventTransaction())
    {
        foreach (var eventType in eventTypes)
        {
            Event ev = engine.ActionManager.BuildEvent(eventType, source);
            transaction.AddEvent(ev);
        }
    }
}

Alarm alarm = m_engine.CreateEntity("My Dynamic Alarm", EntityType.Alarm) as Alarm;
alarm.ReactivationThreshold = TimeSpan.Zero;
Guid createdAlarm = alarm.Guid;
//get the User to notify
User admin = m_engine.GetEntity(new Guid("00000000-0000-0000-0000-000000000003")) as User;
//get the entity
Camera camera = m_engine.GetEntity(new Guid("CE3C6977-F595-43A0-8886-65D370D233A6")) as Camera;
DynamicAlarmContent dac = new DynamicAlarmContent("User Entered Zone");
//Add a URL to help the user with the Alarm Triggered
dac.Urls.Add("www.genetec.com");
//set the Alarm Priority
dac.Priority = 5;
//Attach the entities
dac.AttachedEntities.Add(camera.Guid);
//attach the recipients
dac.ForwardedRecipients.Add(admin.Guid);
//This will trigger the alarm with its Dynamic Content
int alarmResult = m_engine.AlarmManager.TriggerAlarm(alarm.Guid, admin.Guid, dac);
if (alarmResult == -1)
   MessageBox.Show("Alarm was not triggered");

var door = Engine.TransactionManager.ExecuteTransaction(() =>
{
    var unit = (IntrusionUnit)Engine.CreateEntity(entityName, EntityType.IntrusionUnit);
        unit.RunningState = State.Running;
    unit.Capabilities = IntrusionUnitCapabilities.ConfigurableArmingDelay |
                        IntrusionUnitCapabilities.SupportClearLog |
                        IntrusionUnitCapabilities.SupportTimeZone;

    var intrusionArea1 = (IntrusionArea)Engine.CreateEntity("IntrusionArea1", EntityType.IntrusionArea, unit.Guid);
        intrusionArea1.ArmedState = ArmingState.MasterArmed;
    intrusionArea1.RunningState = State.Running;
    intrusionArea1.Capabilities = IntrusionAreaCapabilities.All;

    var input1 = (AlarmPanelInputDevice)Engine.CreateDevice("Input 1", DeviceType.AlarmPanelInput, unit.Guid);
        input1.State = InputState.Normal;
    input1.RunningState = State.Running;
    input1.Name = "Input1";
    input1.Description = "Description of Input1";

    var output2 = (AlarmPanelOutputDevice)Engine.CreateDevice("Output 2", DeviceType.AlarmPanelOutput, unit.Guid);
        output2.State = OutputState.Normal;
    output2.RunningState = State.Running;
    output2.Name = "Output2";
    output2.Description = "Description of Output2";

    var door = (Door)Engine.CreateEntity("Door 1", EntityType.Door);
        door.RunningState = State.Running;

    return door;
});

