Task transactionTask = Engine.TransactionManager.ExecuteTransactionAsync(() =>
{
    var unit = (IntrusionUnit)Engine.CreateEntity(entityName, EntityType.IntrusionUnit);
        unit.RunningState = State.Running;
    unit.Capabilities = IntrusionUnitCapabilities.ConfigurableArmingDelay |
                        IntrusionUnitCapabilities.SupportClearLog |
                        IntrusionUnitCapabilities.SupportTimeZone;

    var intrusionArea1 = (IntrusionArea)Engine.CreateEntity("IntrusionArea1", EntityType.IntrusionArea, unit.Guid);
        intrusionArea1.ArmedState = ArmingState.MasterArmed;
    intrusionArea1.RunningState = State.Running;
    intrusionArea1.Capabilities = IntrusionAreaCapabilities.All;

    var input1 = (AlarmPanelInputDevice)Engine.CreateDevice("Input 1", DeviceType.AlarmPanelInput, unit.Guid);
        input1.State = InputState.Normal;
    input1.RunningState = State.Running;
    input1.Name = "Input1";
    input1.Description = "Description of Input1";

    var output2 = (AlarmPanelOutputDevice)Engine.CreateDevice("Output 2", DeviceType.AlarmPanelOutput, unit.Guid);
        output2.State = OutputState.Normal;
    output2.RunningState = State.Running;
    output2.Name = "Output2";
    output2.Description = "Description of Output2";

    var door = (Door)Engine.CreateEntity("Door 1", EntityType.Door);
        door.RunningState = State.Running;
});

//We await the execution of the transaction
await transactionTask;

//We can check if there was exception by doing
if (transactionTask.Status == TaskStatus.Faulted)
{
    //A task.Exception is an AggregateException.
    foreach (var innerException in transactionTask.Exception.InnerExceptions)
    {
        Console.WriteLine(innerException.Message);
    }
}

member.summary:
Gets the code and the exception message as a formatted string. 
The format is as follow:
Sdk Engine failed to loggon on server '{The server name}'.
Failure code : {The error code}
Exception message : {The message}

The message is only shown when there is a message for the error code.

example.code:
//Complete example showing how to get public/private tasks and getting information about cameras inside the task.

public async void LogIntoTheSdkAsync()
{
    //We need an sdk engine.
    var engine = new Engine();

    //Once logged we can perform our desired actions.
    //For the example we hooked both synchronised and async methods.
    engine.LoggedOn += (sender, e) => GetCameraItemsForTask("Monitoring", engine);
    engine.LoggedOn += (sender, e) => GetCameraItemForTaskAsync("Monitoring", engine);

    //Logging on.
    await Task.Run(() => engine.LogOn("localhost", "username", "password"));
}

private static IEnumerable<IUserTaskItem> GetCameraItemsForTask(string taskName, Engine engine)
{
    //Get the private task from a user instance.
    var privateTasks = engine.LoggedUser.PrivateTasks;

    //Filter with our needs.
    var privateTasksByName = privateTasks.FirstOrDefault(task => string.Compare(task.Name, taskName, StringComparison.OrdinalIgnoreCase) == 0);
    return privateTasksByName?.Content.Where(content => content.Type == EntityType.Camera);
}

private static async Task<IEnumerable<IUserTaskItem>> GetCameraItemForTaskAsync(string taskName, Engine engine)
{
    //Get tasks asynchronously using a user.
    try
    {
        //Get an other user using it's guid (this is a random guid just for the example).
        //If the currently logged user don't have the priviledge a SecurityException is thrown.
        var otherUser = engine.GetEntity<User>(new Guid("7a6sd-2a71-4a9b-993a-847asd40ac"));

        //Asynchronously wait for the result of the methods.
        var privateTasks = await engine.UserTaskManager.GetPrivateTasksAsync(otherUser);

        //Filter with our needs.
        var privateTasksByName = privateTasks.FirstOrDefault(task => string.Compare(task.Name, taskName, StringComparison.OrdinalIgnoreCase) == 0);
        return privateTasksByName?.Content.Where(content => content.Type == EntityType.Camera);
    }
    catch (SecurityException ex)
    {
        //We don't have permission to get the other user.
        throw;
    }
}

member.remarks:
See  to see how more information can be obtained about the event.
When applicable, the  can be cast as one of its derived classes.
The  can also be cast as one of its derived classes to obtain more information about the event.


//for an AccessGrantedEvent
CardholderAccessRequestedEventArgs caRequested = e as CardholderAccessRequestedEventArgs;
Guid ch = caRequested.CardholderGuid;
//for an DoorEntryAssumedEvent
Genetec.Sdk.Events.AccessPoint.DoorEntryAssumedEvent doorEntryAssumedEvent = e.Event as Genetec.Sdk.Events.AccessPoint.DoorEntryAssumedEvent;
Guid ds = doorEntryAssumedEvent.DoorSideGuid;

